mapper.select:
SELECT a.id apiId, a.apiTypeId , IFNULL(c.mobileOperatorId,0) mobileOperatorId, c.apiTypeName, c.mobileOperatorName, c.vendorName, IFNULL(b.totalCount,0) totalCount, IFNULL(b.successCount,0) successCount, IFNULL(b.failCount,0) failCount, IFNULL(b.count1,0) count1, IFNULL(b.count2,0) count2, IFNULL(b.count3,0) count3, IFNULL(b.count4,0) count4, IFNULL(b.count5,0) count5, IFNULL(b.count6,0) count6, IFNULL(b.count7,0) count7 FROM qydata.Api a LEFT JOIN (SELECT apiId, totalCount, successCount, failCount, count1, count2, count3, count4, count5, count6, count7 FROM qyfinance.apiResponseConditionCountByHour WHERE
cycle >= #{currDawn}
AND cycle < #{currTime}
)b ON a.id = b.apiId LEFT JOIN qyfinance.bkvw_api_ApiTypeVendorPartner c ON a.id = c.apiId LEFT JOIN qyfinance.bkvwMobileOperator d ON c.mobileOperatorId = d.id
GROUP BY a.id, a.apiTypeId, c.mobileOperatorId ORDER BY sum(b.totalCount) DESC
