SELECT CONCAT_WS('-',b.name,d.name) apiTypeName_stidName,c.name vendorName,a.sumCount,a.cost FROM ( SELECT a.count sumCount,b.apiTypeId,b.vendorId,b.mobileOperatorId,c.cost FROM ( SELECT a.apiId,sum(a.sumCount) count FROM qyfinance.PartnerOurDayCount a WHERE a.typeId = 1 AND a.apiId IS NOT NULL
AND a.consuTime >= #{beginDate}
AND a.consuTime < #{endDate}
AND a.customerId NOT IN
#{cid}
AND a.partnerId = #{pid}
GROUP BY a.apiId ) a LEFT JOIN qyfinance.bkvw_api_ApiTypeVendorPartner b ON a.apiId = b.apiId LEFT JOIN qyfinance.excel_ApiPrice c ON a.apiId = c.apiId ) a LEFT JOIN qyfinance.bkvwApiType b ON a.apiTypeId = b.id LEFT JOIN qyfinance.finance_ApiVendor c ON a.vendorId = c.id LEFT JOIN qyfinance.bkvwMobileOperator d ON a.mobileOperatorId = d.id WHERE a.sumCount > 0

SELECT a.yearMonth,a.years,a.months,CONCAT_WS('-',b.name,d.name) apiTypeName_stidName,c.name vendorName,a.sumCount,a.cost FROM ( SELECT a.yearMonth,a.years,a.months,a.count sumCount,b.apiTypeId,b.vendorId,b.mobileOperatorId,c.cost FROM ( SELECT DATE_FORMAT(a.consuTime,'%Y-%m') yearMonth, DATE_FORMAT(a.consuTime,'%Y') years, DATE_FORMAT(a.consuTime,'%m') months, a.apiId,sum(a.sumCount) count FROM qyfinance.PartnerOurDayCount a WHERE a.typeId = 1 AND a.apiId IS NOT NULL
AND a.consuTime >= #{beginDate}
AND a.consuTime < #{endDate}
AND a.customerId NOT IN
#{cid}
AND a.partnerId = #{pid}
GROUP BY DATE_FORMAT(a.consuTime,'%Y-%m'),a.apiId ) a LEFT JOIN qyfinance.bkvw_api_ApiTypeVendorPartner b ON a.apiId = b.apiId LEFT JOIN qyfinance.excel_ApiPrice c ON a.apiId = c.apiId ) a LEFT JOIN qyfinance.bkvwApiType b ON a.apiTypeId = b.id LEFT JOIN qyfinance.finance_ApiVendor c ON a.vendorId = c.id LEFT JOIN qyfinance.bkvwMobileOperator d ON a.mobileOperatorId = d.id WHERE a.sumCount > 0

