SELECT CONCAT_WS('-',b.name,c.name) apiTypeName_stidName,a.price cost,a.countSuccess sumCount FROM ( SELECT a.apiTypeId,a.stid,b.price,sum(a.countSuccess) countSuccess FROM ( SELECT b.companyId,a.apiTypeId,ifnull(a.stid,0) stid,a.countSuccess FROM qyfinance.CompanyApiTypeConsumeDayCount a LEFT JOIN qyfinance.bkvwCustomer b ON a.customerId = b.id WHERE a.customerId = #{cid} AND a.countSuccess > 0
AND a.consuTime >= #{beginDate}
AND a.consuTime < #{endDate}
AND a.apiTypeId IN
#{tid}
) a LEFT JOIN qyfinance.excel_CompanyApiPrice b ON a.companyId = b.companyId AND a.apiTypeId = b.apiTypeId AND a.stid = subTypeId GROUP BY a.apiTypeId,a.stid,b.price ) a LEFT JOIN qyfinance.excel_ApiType b ON a.apiTypeId = b.id LEFT JOIN qyfinance.bkvwMobileOperator c ON a.stid = c.id

