AND a.consuTime >= #{beginDate}
AND a.consuTime < #{endDate}
AND a.apiTypeId IN
#{tid}
) a LEFT JOIN qyfinance.excel_CompanyApiPrice b ON a.companyId = b.companyId AND a.apiTypeId = b.apiTypeId AND a.stid = subTypeId GROUP BY DATE_FORMAT(a.consuTime,'%Y-%m'),a.apiTypeId,a.stid,b.price ) a LEFT JOIN qyfinance.excel_ApiType b ON a.apiTypeId = b.id LEFT JOIN qyfinance.bkvwMobileOperator c ON a.stid = c.id

