mapper.select:
select a.customerId, a.apiTypeId, a.subTypeId, IFNULL(sum(a.consumeCount * b.price),0) incomeAccount FROM qyfinance.CustomerConsumeHourCount a LEFT JOIN ( select a.id customerId, b.apiTypeId, b.subTypeId, b.price
FROM qydata.Customer a INNER JOIN qydata.CompanyApi b ON a.companyId = b.companyId where a.typeId = 1 GROUP BY a.Id, b.apiTypeId, b.subTypeId )b ON a.customerId = b.customerId AND a.apiTypeId = b.apiTypeId AND a.subTypeId = b.subTypeId WHERE
a.cycle >= #{Dawn}
AND a.cycle < #{currTime}
GROUP BY a.customerId,a.apiTypeId,a.subTypeId

SELECT IFNULL(a.consumeAccount * b.cost, 0) costAccount FROM qyfinance.ApiConsumeHourCount a LEFT JOIN qydata.Api b ON a.apiId = b.id WHERE
a.cycle >= #{Dawn}
AND a.cycle < #{currTime}

SELECT a.apiTypeId, a.subTypeId, c.`name` apiTypeName, d.`name` subTypeName from ( SELECT a.typeId apiTypeId, a.subTypeId FROM qyfinance.ApiConsumeHourCount a WHERE
a.cycle >= #{Dawn}
AND a.cycle < #{currTime}
UNION SELECT b.apiTypeId, b.subTypeId FROM qyfinance.CustomerConsumeHourCount b WHERE
b.cycle >= #{Dawn}
AND b.cycle < #{currTime}
)a LEFT JOIN qyfinance.bkvwApiType c ON a.apiTypeId = c.Id LEFT JOIN qyfinance.bkvwMobileOperator d ON a.subTypeId = d.id

SELECT a.typeId apiTypeId, a.subTypeId, sum(a.consumeAccount) costCount, SUM(a.costAccount) costAccount FROM ( SELECT a.customerId, a.typeId, a.subTypeId, a.consumeAccount, a.consumeAccount * b.cost costAccount FROM qyfinance.ApiConsumeHourCount a LEFT JOIN qydata.Api b ON a.apiId = b.id WHERE
a.cycle >= #{Dawn}
AND a.cycle < #{currTime}
)a GROUP BY a.typeId,a.subTypeId

select a.apiTypeId, a.subTypeId, sum(a.consumeCount) incomeCount, sum(a.consumeCount * b.price) incomeAccount FROM qyfinance.CustomerConsumeHourCount a LEFT JOIN ( select a.id customerId, b.apiTypeId, b.subTypeId, b.price
FROM qydata.Customer a INNER JOIN qydata.CompanyApi b ON a.companyId = b.companyId where a.typeId = 1 GROUP BY a.Id, b.apiTypeId, b.subTypeId )b ON a.customerId = b.customerId AND a.apiTypeId = b.apiTypeId AND a.subTypeId = b.subTypeId WHERE
a.cycle >= #{Dawn}
AND a.cycle < #{currTime}
GROUP BY a.apiTypeId,a.subTypeId

SELECT a.customerId, a.customerName, a.apiTypeId, a.subTypeId, a.consumeAccount, b.price, IFNULL(a.consumeAccount * b.price, 0) amount FROM ( SELECT a.customerId, a.apiTypeId, a.subTypeId, b. NAME customerName, b.companyId, sum(a.consumeCount) consumeAccount FROM qyfinance.CustomerConsumeHourCount a LEFT JOIN qydata.Customer b ON a.customerId = b.id WHERE b.typeId = 1
AND a.cycle >= #{Dawn}
AND a.cycle < #{currTime}
AND a.apiTypeId = #{apiTypeId} AND a.subTypeId = #{subTypeId} GROUP BY a.customerId,a.apiTypeId,a.subTypeId ) a LEFT JOIN qydata.CompanyApi b ON a.companyId = b.companyId AND a.apiTypeId = b.apiTypeId AND a.subTypeId = b.subTypeId GROUP BY a.customerId, a.apiTypeId, a.subTypeId

SELECT a.vendorId, a.vendorName, a.consumeAccount, a.cost, a.consumeAccount*a.cost amount FROM ( SELECT a.vendorId, c.name vendorName, sum(consumeAccount) consumeAccount, b.cost FROM qyfinance.ApiConsumeHourCount a LEFT JOIN qydata.Api b ON a.apiId = b.id LEFT JOIN qyfinance.finance_ApiVendor c ON c.id = a.vendorId WHERE
a.cycle >= #{Dawn}
AND a.cycle < #{currTime}
AND a.typeId = #{apiTypeId} AND a.subTypeId = #{subTypeId} GROUP BY a.vendorId,a.apiId,a.typeId,a.subTypeId )a
