mapper.select:
SELECT a.vendorId,c.name vendorName,a.partnerId,a.partnerName,a.status,b.balance charge
FROM qyfinance.bkvwApiVendor a
LEFT JOIN qyfinance.ApiVendorBalance b ON a.vendorId = b.vendorId
LEFT JOIN qyfinance.finance_ApiVendor c ON a.vendorId = c.id


       a.status in

            #{status}



        AND a.vendorId=#{vendorId}


        AND a.partnerId=#{partnerId}

SELECT b.vendorId,sum(a.consume) consume FROM ( SELECT apiId,sum(totleCost) consume FROM qyfinance.bkvwApiCostDayCount
AND consuTime >= #{beginDate}
AND consuTime < #{endDate}
GROUP BY apiId ) a LEFT JOIN qyfinance.finance_Api b ON a.apiId = b.id GROUP BY b.vendorId

SELECT a.vendorId,a.apiTypeId,a.apiTypeName,a.status,a.cost,a.mobileOperatorName, b.consume,b.userCount, b.feeCount FROM qyfinance.bkvw_api_ApiTypeVendorPartner a LEFT JOIN ( SELECT a.apiId,sum(a.totleCost) consume,sum(a.usageAmount) userCount,sum(a.feeAmount) feeCount FROM qyfinance.ApiCostDayCount a
AND a.consuTime >= #{beginDate}
AND a.consuTime < #{endDate}
GROUP BY a.apiId ) b ON a.apiId=b.apiId ORDER BY a.apiTypeName ASC,a.status DESC

SELECT a.vendorId, sum(a.consumeAccount * b.cost) total from qyfinance.ApiConsumeHourCount a LEFT JOIN qyfinance.finance_Api b ON a.apiId = b.id WHERE a.cycle >= #{currDawn} AND
a.cycle < #{currentDate}
GROUP BY a.vendorId

SELECT CONCAT_WS('-',d.name,e.name) apiTypeName, c.cost, c.consumeAccount, c.apiTypeConsume FROM ( SELECT a.typeId, a.subTypeId, b.cost, sum(a.consumeAccount) consumeAccount, sum(a.consumeAccount * b.cost) apiTypeConsume, a.cycle from qyfinance.ApiConsumeHourCount a INNER JOIN qydata.Api b ON a.apiId = b.id WHERE a.cycle >= #{currDawn} AND
a.cycle < #{currentDate}
AND a.vendorId = #{vendorId} GROUP BY a.apiId )c LEFT JOIN qyfinance.bkvwApiType d ON c.typeId = d.id LEFT JOIN qyfinance.bkvwMobileOperator e ON e.id =c.subTypeId
